// src/services/taskService.ts
import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL + '/tasks';

const getTasks = async (token: string) => {
    const response = await axios.get(API_URL, {
        headers: { Authorization: `Bearer ${token}` }
    });
    return response.data;
};

const createTask = async (taskData: any, token: string) => {
    const response = await axios.post(API_URL, taskData, {
        headers: { Authorization: `Bearer ${token}` }
    });
    return response.data;
};

const updateTask = async (task: any, token: string) => {
    const response = await axios.put(`${API_URL}/${task.id}`, task, {
        headers: { Authorization: `Bearer ${token}` }
    });
    return response.data;
};

const deleteTask = async (id: string, token: string) => {
    await axios.delete(`${API_URL}/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
    });
};

const toggleTask = async (id: string, token: string) => {
    await axios.patch(`${API_URL}/${id}/toggle`, {}, {
        headers: { Authorization: `Bearer ${token}` }
    });
};

export default {
    getTasks,
    createTask,
    updateTask,
    deleteTask,
    toggleTask
};
