import { logout } from "../features/authSlice";

const API_URL = 'http://localhost:5001/api/auth';

interface AuthResponse {
    token: string;
    user: {
        id: string;
        username: string;
    };
    message?: string;
}

async function register(username: string, password: string): Promise<AuthResponse> {
    try {
        const response = await fetch(`${API_URL}/register`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                username,
                password,
            }),
        });

        if (!response.ok) {
            throw new Error(await response.text());
        }

        return await response.json();
    } catch (error) {
        console.error('Registration failed:', error);
        throw error;
    }
}

async function login(username: string, password: string): Promise<AuthResponse> {
    try {
        const response = await fetch(`${API_URL}/login`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                username,
                password,
            }),
        });

        if (!response.ok) {
            throw new Error(await response.text());
        }

        const data = await response.json();

        if (!data.token || !data.user || !data.user.id || !data.user.username) {
            throw new Error('Invalid response from server');
        }

        if (data.token) {
            localStorage.setItem('user', JSON.stringify(data));
        }

        return data;
    } catch (error) {
        console.error('Login failed:', error);
        throw error;
    }
}

const authService = {
    register,
    login,
    logout,
};

export default authService;
