// src/features/taskSlice.ts
import { createSlice, createAsyncThunk, PayloadAction } from '@reduxjs/toolkit';
import { AppDispatch, RootState } from '@store/store';
import taskService from '../services/taskService';
import { enqueueSnackbar } from 'notistack';


interface Task {
    id: string;
    title: string;
    description: string;
    completed: boolean;
    userId: string;
}

interface TaskState {
    tasks: Task[];
    isLoading: boolean;
    error: string | null;
}

const initialState: TaskState = {
    tasks: [],
    isLoading: false,
    error: null
};

// Async thunks
export const fetchTasks = createAsyncThunk(
    'tasks/fetchAll',
    async (_, { getState }) => {
        const { auth } = getState() as RootState;
        return await taskService.getTasks(auth.token!);
    }
);

export const createTask = createAsyncThunk(
    'tasks/create',
    async (taskData: Omit<Task, 'id'>, { getState }) => {
        const { auth } = getState() as RootState;
        return await taskService.createTask(taskData, auth.token!);
    }
);
export const updateTask = createAsyncThunk(
    'tasks/update',
    async (updatedTask: Task, { getState }) => {
        const { auth } = getState() as RootState;
        const response = await taskService.updateTask(updatedTask, auth.token!);
        return response.data;
    }
);
export const updateTaskOnServer = createAsyncThunk(
    'tasks/update',
    async (task: Task, { getState }) => {
        const { auth } = getState() as RootState;
        return await taskService.updateTask(task, auth.token!);
    }
);

export const deleteTaskOnServer = createAsyncThunk(
    'tasks/delete',
    async (id: string, { getState }) => {
        const { auth } = getState() as RootState;
        await taskService.deleteTask(id, auth.token!);
        return id;
    }
);

export const toggleTaskOnServer = createAsyncThunk(
    'tasks/toggle',
    async (id: string, { getState }) => {
        const { auth } = getState() as RootState;
        await taskService.toggleTask(id, auth.token!);
        return id;
    }
);

const taskSlice = createSlice({
    name: 'tasks',
    initialState,
    reducers: {},
    extraReducers: (builder) => {
        builder
            // Handle all pending states
            .addMatcher(
                (action) => action.type.endsWith('/pending'),
                (state) => {
                    state.isLoading = true;
                    state.error = null;
                }
            )
            // Handle all rejected states
            .addMatcher(
                (action) => action.type.endsWith('/rejected'),
                (state, action) => {
                    state.isLoading = false;
                    state.error = action.error.message || 'Failed to complete task';
                    enqueueSnackbar(state.error, { variant: 'error' });
                }
            )
            // Specific success cases
            .addCase(fetchTasks.fulfilled, (state, action) => {
                state.tasks = action.payload;
                state.isLoading = false;
            })
            .addCase(createTask.fulfilled, (state, action) => {
                state.tasks.push(action.payload);
                state.isLoading = false;
                enqueueSnackbar('Task created', { variant: 'success' });
            })
            .addCase(updateTaskOnServer.fulfilled, (state, action) => {
                const index = state.tasks.findIndex(t => t.id === action.payload.id);
                if (index !== -1) {
                    state.tasks[index] = action.payload;
                }
                state.isLoading = false;
                enqueueSnackbar('Task updated', { variant: 'success' });
            })
            .addCase(deleteTaskOnServer.fulfilled, (state, action) => {
                state.tasks = state.tasks.filter(t => t.id !== action.payload);
                state.isLoading = false;
                enqueueSnackbar('Task deleted', { variant: 'success' });
            })
            .addCase(toggleTaskOnServer.fulfilled, (state, action) => {
                const task = state.tasks.find(t => t.id === action.payload);
                if (task) {
                    task.completed = !task.completed;
                }
                state.isLoading = false;
            });
    }
});
export const taskActions = {
    updateTask,
    fetchTasks,
    createTask,
    deleteTaskOnServer,
    toggleTaskOnServer,
};
export default taskSlice.reducer;
