import axios from 'axios';

const API_URL = 'http://localhost:5000/api/tasks';

const getTasks = async (token: string) => {
    const config = {
        headers: {
            Authorization: `Bearer ${token}`,
        },
    };
    const response = await axios.get(API_URL, config);
    return response.data;
};

const createTask = async (title: string, description: string, token: string) => {
    const config = {
        headers: {
            Authorization: `Bearer ${token}`,
        },
    };
    const response = await axios.post(
        API_URL,
        { title, description },
        config
    );
    return response.data;
};

const updateTask = async (task: { id: string; title: string; description: string }, token: string) => {
    const config = {
        headers: {
            Authorization: `Bearer ${token}`,
        },
    };
    const response = await axios.put(
        `${API_URL}/${task.id}`,
        task,
        config
    );
    return response.data;
};

const deleteTask = async (id: string, token: string) => {
    const config = {
        headers: {
            Authorization: `Bearer ${token}`,
        },
    };
    await axios.delete(`${API_URL}/${id}`, config);
};

const toggleTask = async (id: string, token: string) => {
    const config = {
        headers: {
            Authorization: `Bearer ${token}`,
        },
    };
    const response = await axios.patch(
        `${API_URL}/${id}/toggle`,
        {},
        config
    );
    return response.data;
};

const taskService = {
    getTasks,
    createTask,
    updateTask,
    deleteTask,
    toggleTask,
};

export default taskService;
